import React, { useState } from 'react';
import OpenAI from 'openai';

const API_KEY = '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';

function TranslationApp() {
  const [inputText, setInputText] = useState('');
  const [translatedText, setTranslatedText] = useState('');

  const handleInputChange = (event) => {
    setInputText(event.target.value);
  };

  const handleTranslate = async () => {
    try {
      const openai = new OpenAI({ apiKey: API_KEY });
      const response = await openai.translate({
        text: inputText,
        source_language: 'en', // 원본 언어 (예: 영어)
        target_language: 'ko', // 번역할 언어 (한국어)
      });
      setTranslatedText(response.data.translations[0].translated_text);
    } catch (error) {
      console.error('번역 오류:', error);
    }
  };

  return (
    <div>
      <h1>번역 서비스</h1>
      <textarea
        value={inputText}
        onChange={handleInputChange}
        placeholder="번역할 텍스트 입력"
      />
      <button onClick={handleTranslate}>번역</button>
      {translatedText && (
        <div>
          <h3>번역 결과:</h3>
          <p>{translatedText}</p>
        </div>
      )}
    </div>
  );
}

export default TranslationApp;
